// page init
jQuery(function(){
	initGetMobileHeight();
	initMobileNav();
	initStickyBlock();
	initAnchors();
	initOpenClose();
	initInVieport();
	initSlickCarousel();
	initCurrentYear();
});


// mobile height init
function initGetMobileHeight() {
	// First we get the viewport height and we multiple it by 1% to get a value for a vh unit
	let vh = window.innerHeight * 0.01;
	// Then we set the value in the --vh custom property to the root of the document
	document.documentElement.style.setProperty('--vh', vh+"px");

	// We listen to the resize event
	window.addEventListener('resize', function() {
		// We execute the same script as before
		let vh = window.innerHeight * 0.01;
		document.documentElement.style.setProperty('--vh', vh+"px");
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('html').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener',
		menuDrop: '.nav-holder',
		onInit: function(self) {
			self.drop.find('.nav-close').on('click', function(e) {
				e.preventDefault();
				self.hide();
			});
			self.drop.find('#nav').on('click', function() {
				self.hide();
			});
		}
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: 'a[href^="#"]:not([href="#"])',
		extraOffset: function() {
			var totalHeight = 0;
			jQuery('#header').each(function() {
				var $box = jQuery(this);
				var stickyInstance = $box.data('StickyScrollBlock');
				if (stickyInstance) {
					stickyInstance.stickyFlag = false;
					stickyInstance.stickyOn();
					totalHeight += $box.outerHeight();
					stickyInstance.onResize();
				} else {
					totalHeight += $box.css('position') === 'fixed' ? $box.outerHeight() : 0;
				}
			});
			return totalHeight;
		},
		wheelBehavior: 'none'
	});

	var anchorInstance2;
	ResponsiveHelper.addRange({
		'1024...': {
			on: function() {
				anchorInstance2 = new SmoothScroll({
					anchorLinks: 'a[href^="#"]:not([href="#"])',
					extraOffset: 100,
					activeClasses: 'link',
					sectionActiveClass: '',
					wheelBehavior: 'none'
				});
			},
			off: function() {
				anchorInstance2.destroy();
			}
		}
	});
}

// init sticky header
function initStickyBlock() {
	var lastScrollTop = 0;
	jQuery(window).scroll(function(event){
		 var st = jQuery(this).scrollTop();
		 if (st > lastScrollTop){
				// downscroll code
				jQuery(".header").removeClass("scroll-up");
				jQuery(".header").addClass("scroll-down");
		 } else if (st < 50) {
				// downscroll code
				jQuery(".header").removeClass("scroll-up");
		 } else {
				// upscroll code
				$(".header").removeClass("scroll-down");
				$(".header").addClass("scroll-up");
			}
		 lastScrollTop = st;
	});
}

// open-close init
function initOpenClose() {
	jQuery('li.open-close').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// block in viewport init
function initInVieport() {
	jQuery('.viewport').itemInViewport({
		activeClass: 'in-viewport',
		once: true
	});
	jQuery('.counter').itemInViewport({
		activeClass: 'in-viewport',
		once: true,
		onShow: function(el) {
			el.$el.each(function() {
				var counter = jQuery(this).find('.number');
				var prefix =  counter.data('prefix');
				var suffix =  counter.data('suffix');
				var options = {
					prefix : prefix,
					suffix : suffix,
						useEasing : true,
						useGrouping : true,
						separator : ' ',
						decimal : '.'
				};
				var val = parseInt(counter.text(), 10);
				var newCounter = new CountUp(counter[0], 0, val, 0, 1, options);
				newCounter.start();
			});
		}
	});
}

// slick init
function initSlickCarousel() {
	jQuery('.slick-slider').slick({
		slidesToScroll: 1,
		prevArrow: '<button class="slick-prev">Previous</button>',
		nextArrow: '<button class="slick-next">Next</button>',
		dots: false,
		// autoplay: true,
		// autoplaySpeed: 5000,
		fade: true
	});
}

// copyright current year init
function initCurrentYear() {
	jQuery('#currentYear').text((new Date).getFullYear());
}

